INSERT INTO `productinfo` VALUES (6, 30, 0, 1, 'Cisco Catalyst 3750系列交换机 ', '', '<div>\r\n	<p>\r\n		<span lang="EN-US">C</span><span lang="EN-US">i</span><span lang="EN-US">s</span><span lang="EN-US">c</span><span lang="EN-US">o <span lang="EN-US">C</span><span lang="EN-US">a</span><span lang="EN-US">t</span><span lang="EN-US">a</span><span lang="EN-US">l</span><span lang="EN-US">y</span><span lang="EN-US">s</span><span lang="EN-US">t <span lang="EN-US">3750 系列交换机是一款适用于中型 机构和大型企业分支机构的创新产品。该交换机采 用了 <span lang="EN-US">C</span><span lang="EN-US">i</span><span lang="EN-US">s</span><span lang="EN-US">c</span><span lang="EN-US">o</span><span lang="EN-US"> </span><span lang="EN-US">S</span><span lang="EN-US">t</span><span lang="EN-US">a</span><span lang="EN-US">c</span><span lang="EN-US">k</span><span lang="EN-US">W</span><span lang="EN-US">i</span><span lang="EN-US">s</span><span lang="EN-US">e</span><span lang="EN-US"> </span>技术，通过结合易用性和可 堆叠交换机的最高永续性，提高了局域网运行效率。</span></span></span></p>\r\n	<p align="left">\r\n		<b>适用于需要下列特性的企业</b></p>\r\n	<p align="left">\r\n		<span lang="EN-US">C</span><span lang="EN-US">i</span><span lang="EN-US">s</span><span lang="EN-US">c</span><span lang="EN-US">o</span><span lang="EN-US"> </span><span lang="EN-US">C</span><span lang="EN-US">a</span><span lang="EN-US">t</span><span lang="EN-US">a</span><span lang="EN-US">l</span><span lang="EN-US">y</span><span lang="EN-US">s</span><span lang="EN-US">t</span><span lang="EN-US"> </span><span lang="EN-US">3750 系列交换机<span lang="EN-US"> </span></span></p>\r\n	<p align="left">\r\n		<span lang="EN-US">● 提高企业生产率、永续性、安全性和可扩展性</span></p>\r\n	<p align="left">\r\n		<span lang="EN-US">● 快速部署且方便地管理网络</span></p>\r\n	<p align="left">\r\n		● 部署 <span lang="EN-US">I</span><span lang="EN-US">P</span><span lang="EN-US"> 语音、无线接入或千兆到桌面（<span lang="EN-US">G</span><span lang="EN-US">T</span><span lang="EN-US">TD</span>）</span></p>\r\n	<p align="left">\r\n		<span lang="EN-US">C</span><span lang="EN-US">i</span><span lang